function semester_friendly_name($semester) {
    $year = intval(substr($semester, 0, 4));
    $semesterCode = substr($semester, 4);

    switch ($semesterCode) {
        case '01':
            return 'חורף ' . $year . '-' . ($year + 1);

        case '02':
            return 'אביב ' . ($year + 1);

        case '03':
            return 'קיץ ' . ($year + 1);

        default:
            return $semester;
    }
}

